import { Component } from '@angular/core';
import { Nota } from '../domain/nota';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { NotasService } from '../services/notas.service';
import { NotasFirebaseService } from '../services/notas-firebase.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-crear',
  templateUrl: './crear.component.html',
  styleUrls: ['./crear.component.css']
})
export class CrearComponent {
  formulario: FormGroup;
  nota : Nota = new Nota()

  constructor(private router: Router ,private fb: FormBuilder, private notasService: NotasService, private notasFirebaseService: NotasFirebaseService) {
    this.formulario = this.fb.group({
      nota: ['', Validators.required],
      fecha: [null, Validators.required],
      etiqueta: ['', Validators.required],
    });
    let params = this.router.getCurrentNavigation()?.extras.queryParams;
      if(params){
        console.log(params)
        this.nota = params['nota']
      }
  }

  onSubmit() {
    if (this.formulario.valid) {
      this.notasService.agregarReceta(this.nota);
      this.notasFirebaseService.save(this.nota);
      this.formulario.reset();
      }
    }
}

<form [formGroup]="formulario" (ngSubmit)="onSubmit()">
    <div class="form-group">
      <label for="nota">Nota:</label>
      <input type="text" id="nota" formControlName="nota" [(ngModel)]="nota.nota" required>
    </div>

    <div class="form-group">
      <label for="fecha">Fecha:</label>
      <input type="date" id="fecha" formControlName="fecha" [(ngModel)]="nota.fecha" required>
    </div>


    <div class="form-group">
      <label for="etiqueta">Etiqueta:</label>
      <textarea id="etiqueta" formControlName="etiqueta" [(ngModel)]="nota.etiqueta" required></textarea>
    </div>

    <button type="submit" [disabled]="formulario.invalid">Agregar Nota</button>
</form>
