import { Component, OnInit } from '@angular/core';
import { FacturaService } from 'src/app/services/factura.service';
import { Factura } from '../domain/factura';
import { Cliente } from '../domain/cliente';
import { DetalleService } from 'src/app/services/detalle.service';
import { Detalle } from '../domain/detalle';

@Component({
  selector: 'app-facturas',
  templateUrl: './facturas.component.html',
  styleUrls: ['./facturas.component.css']
})
export class FacturasComponent implements OnInit{
  facturas: any
  detalles: any
  facturaSeleccionada: Factura | null = null;
  detallesFacturaSeleccionada: Detalle[] = [];

  factura: Factura = new Factura()

  detallesSeleccionados: Detalle[] = [];

  constructor(private facturasServices: FacturaService, private detallesService: DetalleService){}

  ngOnInit(): void {
    this.facturas = this.facturasServices.getFacturas()
    this.detalles = this.de
  }

  cargarDetalles() {
    this.facturasServices.getAllDetalles().subscribe(
      (data: Detalle[]) => {
        this.detalles = data;
      },
      (error) => {
        console.error('Error al obtener detalles', error);
      }
    );
  }

  toggleDetalle(detalle: Detalle) {
    const index = this.detallesSeleccionados.indexOf(detalle);
    if (index > -1) {
      this.detallesSeleccionados.splice(index, 1); // Quitar detalle
    } else {
      this.detallesSeleccionados.push(detalle); // Añadir detalle
    }
  }

  cargarDetallesFactura() {
    if (this.facturaSeleccionada && this.facturaSeleccionada.id !== undefined) {
      const codigoF: number = this.facturaSeleccionada.id;
      this.facturasServices.getDetalles(codigoF).subscribe(
        (data: Detalle[]) => {
          this.detallesFacturaSeleccionada = data;
        },
        (error) => {
          console.error('Error al obtener detalles de la factura', error);
        }
      );
    }
  }

  guardar(){
    this.facturasServices.saveFacturas(this.factura, this.detallesSeleccionados).subscribe(data => {
      console.log(data)
      if (data) {
        // Si data existe y es un objeto, ahora puedes comprobar si data.codigo es 1
        if(data.codigo == 1) {
          this.ngOnInit(); // Aunque llamar a ngOnInit() directamente no es una buena práctica
        } else {
          alert("Error al insertar " + data.mensaje);
        }
      } else {
        alert("Error al insertar: La respuesta es nula.");
      }
    })
  }
}
<h1>Facturas</h1>

<form>
    <table>
        <tr>
            <td>Id:</td>
            <td><input type="number" name="txtId"  [(ngModel)]="factura.id" aria-label="ID de Factura"></td>
        </tr>
        <tr>
            <td>Fecha:</td>
            <td><input type="text" name="txtFecha"  [(ngModel)]="factura.fecha" aria-label="Fecha de Factura"></td>
        </tr>
        <tr>
            <td>Cedula:</td>
            <td><input type="text" name="txtCliente"  [(ngModel)]="factura.cedula" aria-label="Cliente de Factura"></td>
        </tr>
    </table>

    <ul>
        <li *ngFor="let detalle of detalles">
          <input type="checkbox" [value]="detalle" (change)="toggleDetalle(detalle)" aria-label="detalles"> {{detalle.nombre}}
        </li>
    </ul>
</form>

<button (click)="guardar()">Guardar</button>

<table>
    <tr>
      <th>ID</th>
      <th>Fecha</th>
      <th>Cliente</th>
      <th>Acciones</th> <!-- Nueva columna para el botón -->
    </tr>
    <tr *ngFor="let fac of facturas | async">
      <td>{{fac.id}}</td>
      <td>{{fac.fecha}}</td>
      <td>{{fac.cliente.nombre}}</td>
      <td>
        <!-- Agrega un botón para mostrar los detalles -->
        <button (click)="cargarDetallesFactura()">Mostrar Detalles</button>
      </td>
    </tr>
</table>

<div *ngFor="let fac of facturas | async">
    <h2>Detalles de la Factura</h2>
    <p>{{fac.detalle?.nombre}} - Cantidad: {{fac.detalle?.cantidad}}</p>
    <!-- Mostrar más información de los detalles según tus necesidades -->
</div>
