import { Component, OnInit } from '@angular/core';
import { NavigationExtras, Router } from '@angular/router';
import { Nota } from '../domain/nota';
import { NotasFirebaseService } from '../services/notas-firebase.service';
import { NotasService } from '../services/notas.service';

@Component({
  selector: 'app-visualizar',
  templateUrl: './visualizar.component.html',
  styleUrls: ['./visualizar.component.css']
})
export class VisualizarComponent implements OnInit {
  notas: Nota[] = [];
  terminoBusqueda: string = '';

  listaNotas: any

  constructor(private router: Router, private notasService: NotasService, private notasFirebaseService: NotasFirebaseService) {
    this.notas = notasService.obtenerNotas()

    this.listaNotas = this.notasFirebaseService.getAll()
  }

  ngOnInit() {
    this.notas = this.notasService.obtenerNotas();
  }

  borrarTodasLasNotas() {
    this.notasService.borrarNotas();
    this.notasFirebaseService.eliminarCampo();
  }

  goEditar(receta: any){
    console.log("editando", receta)

    let params: NavigationExtras = {
      queryParams: {
        receta: receta
      }
    }
    this.router.navigate(['paginas/formulario'], params)
  }
}
<html lang="es">
<head>
    <title>Página Web de Recetas Culinarias</title>
    <meta charset="utf-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
</head>
<body>
    <button (click)="borrarTodasLasNotas()">Borrar Notas</button>
    <h2>Notas</h2>
<ul>
  <li *ngFor="let nota of listaNotas | async">
    <!--<a (click)="goEditar(receta)">{{ receta.nombre }}</a>-->
    <p>Ingredientes: {{ nota.nota }}</p>
    <p>Pasos: {{ nota.fecha }}</p>
    <h3 routerLink="/paginas/crear/{{nota.etiqueta}}">{{nota.nombre}}</h3>
  </li>
</ul>
</body>
