import { Component, OnInit } from '@angular/core';
import { Nota } from '../domain/nota';
import { ActivatedRoute, Router } from '@angular/router';
import { NotasFirebaseService } from '../services/notas-firebase.service';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-editar',
  templateUrl: './editar.component.html',
  styleUrls: ['./editar.component.css']
})
export class EditarComponent implements OnInit {
  nota: any;
  formulario: FormGroup;


  constructor(private fb: FormBuilder,  private router: Router, 
      private route: ActivatedRoute, private afs: AngularFirestore,
      private notasFirebaseService: NotasFirebaseService){

        this.route.params.subscribe(params => {
          console.log(params)
          if(params['etiqueta']){
            this.loadPersona(params['etiqueta'])
          }
        })
        this.formulario = this.fb.group({
          nota: ['', Validators.required],
          fecha: ['', Validators.required],
          etiqueta: ['', Validators.required]
        });
  }

  loadPersona(uid: string) {
    this.notasFirebaseService.getNota(uid).subscribe(data => {
      console.log(data)
      this.nota = <any> data
    })
  }

  ngOnInit() {
    // Lógica para obtener la nota existente desde Firestore (asígnala a this.nota)
    // Puedes usar el ID del documento o cualquier otro criterio para obtener el documento específico
    this.afs.collection('tuColeccion').doc('idDelDocumento').valueChanges().subscribe((data: any) => {
      this.nota = data;
      // Asegúrate de asignar los valores actuales al formulario al iniciar
      this.formulario.patchValue({
        nota: this.nota.nota,
        fecha: this.nota.fecha,
        etiqueta: this.nota.etiqueta
      });
    });
  }

  guardarEdicion() {
    // Obtén los nuevos valores desde el formulario
    const nuevaNota = this.formulario.get('nota').value;
    const nuevaFecha = this.formulario.get('fecha').value;
    const nuevaEtiqueta = this.formulario.get('etiqueta').value;

    // Actualiza el documento en Firestore
    this.afs.collection('tuColeccion').doc('idDelDocumento').update({
      nota: nuevaNota,
      fecha: nuevaFecha,
      etiqueta: nuevaEtiqueta
    }).then(() => {
      console.log('Documento actualizado correctamente.');
    }).catch(error => {
      console.error('Error al actualizar el documento:', error);
    });
  }

}
