import { Component } from '@angular/core';
import { Nota } from '../domain/nota';
import { ActivatedRoute, Router } from '@angular/router';
import { NotasFirebaseService } from '../services/notas-firebase.service';

@Component({
  selector: 'app-editar',
  templateUrl: './editar.component.html',
  styleUrls: ['./editar.component.css']
})
export class EditarComponent {
  nota : Nota[] = []

  listaTareas: any
  tareaSeleccionada: Nota | null = null;

  constructor(private notaService : TareaServices,
    private tareaFirebase : TareaFirebaseServices,
      private router: Router){
    this.nota = tareaService.getTarea()

    this.listaTareas = this.tareaFirebase.getAll()
  }

  goEditar(tarea: Nota){
    console.log("editando", tarea)

    let params: NavigationExtras = {
      queryParams: {
        tarea: tarea
      }
    }

    this.router.navigate(['paginas/tarea'], params)
  }

  goDelete(tarea : Nota){
    this.tareaFirebase.delete(tarea.uid)
  }


}
